import React, { Component } from 'react'
import axios from 'axios';
import { TextField, ThemeProvider } from '@material-ui/core'
// import DatePicker from 'react-datepicker';
import "react-datepicker/dist/react-datepicker.css"
import { ToastContainer, toast, Zoom, Bounce, Flip } from "react-toastify"
import "react-toastify/dist/ReactToastify.css"
import '../../App.css';



toast.success("Welcome to Leave Requests", {
  position: toast.POSITION.BOTTOM_CENTER,
  draggable: true,
  transition: Flip,
  autoClose: 5000,
  closeOnClick: true
});

export default class Leave extends Component {

    constructor(props) {
        super(props);

        this.onchangerefno = this.onchangerefno.bind(this);
        this.onchangereasonforleave = this.onchangereasonforleave.bind(this);
        this.onchangedate = this.onchangedate.bind(this);

        this.state = {
          refno: '',
          reasonforleave: '',
          date: ''
        }
      }
      
    


      onchangerefno(e) {
        this.setState({
          refno: e.target.value,
        });
      }
    
      onchangereasonforleave(e) {
        this.setState({
          reasonforleave: e.target.value,
        });
      }
    
      onchangedate(e) {
        this.setState({
          date: e.target.value,
        });
      }
    

      //validation
      validate = () => {
        let isError = false;
        const errors = {
            refnoError: '',
            reasonforleaveError: '',
            dateError: ''
        };


        if (this.state.refno.length <= 0) {
            isError = true;
            errors.refnoError = "Reference Number cannot be blank"
            this.state.error1 = true
            toast.error("Reference Number cannot be blank", {
                transition: Flip
            });
        }
        else
            this.state.error1 = false;

        if (!this.state.reasonforleave) {
            isError = true;
            errors.reasonforleaveError = "Reason for leave invalid"
            this.state.error2 = true
            toast.error("Reason for leave Error", {
                transition: Flip
            });;
        } else
            this.state.error2 = false;

          


        if (!this.state.date) {
            this.state.error3 = true
            errors.dateError = "Date cannot be blank"
            toast.error("Date is empty", {
                transition: Flip
            });;
          } else if (!this.state.date.length == 6) {
                this.state.error3 = true;
                errors.dateError = "Date must be in MM/DD/YYYY format"
          } else
            this.state.error3 = false

      this.setState({
            ...this.state,
            ...errors
        });

        return isError;
    };

    //end of validation

    handleReset = () => {
        Array.from(document.querySelectorAll('input'));
        this.setState({
          refno: '',
          reasonforleave: '',
          date: ''
          
        });
    };
     
    

      onSubmit(e) {
        e.preventDefault();
        const err = this.validate();

        if (!err) {
        const leave = {
          refno: this.state.refno,
          reasonforleave: this.state.reasonforleave,
          date: this.state.date          
        }

        console.log(leave);
        toast.success("Leave Successfully Submitted.", {
          position: toast.POSITION.TOP_CENTER,
          draggable: true,
          transition: Flip,
          autoClose: 5000
      });

        axios.post('http://localhost:5000/leave-details', leave).then(res => console.log(res.data));

        this.setState({
          refno: '',
          reasonforleave: '',
          date: ''
        
        })  
             
      }
    }

     



render(){


  

    return(
      
      <>
      <div className="leave1" >
          <>

              <ToastContainer draggable={false} transition={Zoom} autoClose={8000} newestOnTop />
          </>
          <div className="card text-white  bg-light mb-3 " style={{ marginLeft: 0.1 + 'rem' }} >
              <div className="card-header bg-dark"><h3>Add a Leave Request</h3></div>
              <div className="card-body " >
              </div >
              <div className="container">
                  <form onSubmit={this.onSubmit} action="/leave" noValidate='true'>
                      <div className="row">
                          <div className="col form-group" >

                              <TextField
                                  label="Reference Number"
                                  required
                                  color="secondary"
                                  type="text"
                                  fullWidth
                                  variant="outlined"
                                  error={this.state.error1}
                                  value={this.state.refno}
                                  name="refNo"
                                  onChange={this.onchangerefno}
                              />

                              <br></br><br></br>
                              <span className="text-danger">{this.state.refnoError}</span>
                          </div>

                          <br></br><br></br>
                          <div className="row" >
                              <div className="form-group">

                                  <TextField
                                      type="text"
                                      label="Reason for leave"
                                      variant="outlined"
                                      color="secondary"
                                      required
                                      fullWidth
                                      name="reasonforleave"
                                      error={this.state.error2}
                                      value={this.state.reasonforleave}
                                      onChange={this.onchangereasonforleave} />

                                  <br></br>
                                  <span className="text-danger">{this.state.reasonforleaveError}</span>
                              </div>
                          </div>
                      </div>
                      <br></br>
                      <div className="row first-Name">
                          <div className="col form-group" >
                              <TextField
                                  type="text"
                                  variant="outlined"
                                  required
                                  fullWidth
                                  name="date"
                                  color="secondary"
                                  label="MM/DD/YY"
                                  value={this.state.date}
                                  onChange={this.onchangedate}
                                  error={this.state.error3} />
                              <br></br>
                              <span className="text-danger">{this.state.dateError}</span>

                          </div>
                       
                      </div>
                     
                      
                      
                      <div className="form-group">
                          <input type="submit" name="submit" style={{ margin: 'auto', marginLeft: 0.5 + 'rem' }} value="Submit" className="btn btn-outline-danger btn btn-dark" />
                          <input type="reset" style={{ marginLeft: 0.5 + 'rem' }} value="Reset" className="btn btn-outline-warning btn btn-dark" onClick={this.handleReset} />
   
                      </div>
                  </form>
              </div >

          </div >
      </div >
  </>


              
    )

    
    

}


}
