import React, { Component } from "react";
import FormSuper from "../../components/common/formsuper";
import Joi, { join } from "joi-browser";
import axios from "axios";
import ItemCodeTable from "../../components/common/itemcodestable";
import SearchBox from "../../components/common/searchBox";
import ListGroup from "../../components/common/listgroup";

class AddLeave extends FormSuper {
  state = {
    data: {
      refno: "",
      reasonforleave: "",
      date: "",
    },
    categories: ["category1", "category2", "category3"],
    types: ["Product", "Material"],
    iCodes: [],
    genres: ["All", "Product", "Material"],
    searchQuery: "",
    errors: {},
    selectedGenre: "All",
  };

  schema = {
    refno: Joi.string().required(),
    reasonforleave: Joi.string().required(),
    date: Joi.string().required(),
    
  };

  componentDidMount() {
    //take the category set from db
    //take the Material codes from db
    //take the Product codes from db
    //and put them into single array name iCodes after put them into codes table after set search and fiter them and put again
  }

  handleGenreSelect = (g) => {
    this.setState({ selectedGenre: g, searchQuery: "" });
  };

  handleSearch = (query) => {
    this.setState({
      searchQuery: query,
      selectedGenre: "All",
    });
  };

  doSubmit() {
    // console.log("submitted", this.state.data);
    const jsonOb = this.state.data;
    axios
      .post("http://localhost:5000/api/leave-details", jsonOb)
      .then((result) => console.log(result.data));

    axios
      .post("http://localhost:5000/api/leave-details", jsonOb)
      .then((result) => console.log(result.data));

    //this.props.history.push("/items");
    window.location = "/leave";
  }

  render() {
    return (
      <React.Fragment>
        <div className="row">
          <div className="col">
            <h1>New Item</h1>
            <form onSubmit={this.handleSubmit}>
              {this.renderInput("refno", "Reference Number",this.state.refno)}
              {this.renderSelect("reasonforleave", "Reason for Leave", this.state.reasonforleave)}
              {this.renderSelect("date","Date",this.state.date)}
             
              {this.renderButton("Submit")}
            </form>
          </div>

          <div className="col">
            <div className="row">
              <div className="col">
                <ListGroup
                  genres={this.state.genres}
                  onGenreSelect={this.handleGenreSelect}
                  selectedGenre={this.state.selectedGenre}
                />
              </div>
              <div className="col">
                <SearchBox
                  onChange={this.handleSearch}
                  value={this.state.searchQuery}
                  placeHolder="Search codes..."
                />
              </div>
            </div>

            <ItemCodeTable />
          </div>
        </div>
      </React.Fragment>
    );
  }
}

export default AddLeave;
