import React, { Component } from "react";
import {EditEmployeeModal} from '../Modals/EditEmployeeModal';



const LeaveTable = ({ filteredItems, onDelete}) => {
  return (
    <React.Fragment>

<table className="table table-bordered" style={{overflow: "hidden"}}>
      <thead>
        <tr style={{ backgroundColor: "#7121AD", color: "white" }}>
          <th scope="col">Ref No</th>
          <th scope="col">Reason for leave</th>
          <th scope="col">Date</th>
          <th scope="col">Action</th>
        </tr>
      </thead>
    <tbody style={{overflow: "hidden"}}>
        {
          filteredItems.map(leave => (

      <tr key={leave._id}>
        <td>{leave.refno}</td>
        <td>{leave.reasonforleave}</td>
        <td>{leave.date}</td>
        <td>
            <button style={{ backgroundColor: "#7121AD", color: "white" }} className="btn btn-success" onClick={() => this.setState({ editModalShow: true, id: leave._id,
                      refno: leave.refno, 
                      reasonforleave: leave.reasonforleave, 
                      date: leave.date })}
                        >Edit</button>
            <button style={{ backgroundColor: "#BA0D32 ", color: "white" }} className="btn btn-danger" onClick={() => onDelete(leave)}>Delete</button>
        </td>

      </tr>              
            
          ))
        }


     
    </tbody>
    </table>

    </React.Fragment>
  );
};

export default LeaveTable;
