import React, { Component } from "react";
import { ToastContainer, toast, Zoom, Bounce, Flip } from "react-toastify"
import 'react-toastify/dist/ReactToastify.css';
import '../../App.css';
// import Table from "./common/table";
import axios from "axios";
// import { Link } from "react-router-dom";
//import ListGroup from "./common/listgroup";


toast.success("Welcome to Leave Requests.", {
  position: toast.POSITION.BOTTOM_RIGHT,
  draggable: true,
  transition: Flip,
  autoClose: 5000,
  closeOnClick: true
});


class Leave extends Component {

  componentDidMount() {
    this.findAllLeaves();
}
findAllLeaves() {
    axios.get('http://localhost:5000/api/leave-details')
        .then(res => res.data)
        .then((data) => {
            this.setState({
                leavedetails: data
            });
        });
};










  render(){
  return (
    
    <div className='leave'>
      <ToastContainer draggable={false} transition={Zoom} autoClose={8000} newestOnTop />
      <h1>Leave Requests</h1>
    </div>

   






  )
  }
};

export default Leave;
