import React, { Component } from "react";
import {EditEmployeeModal} from '../Modals/EditEmployeeModal';



export class LeaveTable extends Component{
  constructor(props) {
    super(props);
    this.state = { editModalShow: false }
  }
  render(){
    const {id, name, status, version } = this.state;
    let EditModelClose = () => this.setState({ editModalShow: false })
  return (
    <React.Fragment>
  <EditEmployeeModal
                      show={this.state.editModelShow}
                      empleave= {this.state.empLeave}
                      // onHide={EditModelClose}
                    />
<table className="table table-bordered" style={{overflow: "hidden"}}>
      <thead>
        <tr style={{ backgroundColor: "#7121AD", color: "white" }}>
          <th scope="col">Ref No</th>
          <th scope="col">Reason for leave</th>
          <th scope="col">Date</th>
          <th scope="col">Action</th>
        </tr>
      </thead>
    <tbody style={{overflow: "hidden"}}>
        {
          filteredItems.map(leave => (

      <tr key={leave._id}>
        <td>{leave.refno}</td>
        <td>{leave.reasonforleave}</td>
        <td>{leave.date}</td>
        <td>
            <button onClick={() => this.setState({ editModalShow: true, id: i._id, name: i.categoryName, status: i.status })} 
                    style={{ backgroundColor: "#7121AD", color: "white" }} className="btn" onClick= {() => onSet(leave)}
                        >Edit</button>
            <button style={{ backgroundColor: "#BA0D32 ", color: "white" }} className="btn" onClick={() => onDelete(leave)}>Delete</button>
        </td>

      </tr>              
            
          ))
        }


     
    </tbody>
    </table>

    </React.Fragment>
  );
      }
};

export default LeaveTable;
