import React, { Component } from "react";
import axios from 'axios';


class Profile extends Component {

  state = {
    Payments: [],
    empPayments:{}, 
    id: ""
  };


  componentDidMount()  {

    axios
      .get("http://localhost:5000/api/payment-details")
      .then((result) => {
        const Payments = result.data;
        console.log(Payments);

        this.setState({ Payments: Payments });
      })
      .catch((err) => console.log(err.message));
  }

  setNewDetails = (payments) => {
    this.setState({ empPayments: payments});
  }

  handlePaymentsDelete = (payments) => {
    // console.log("Delete");
    const Payments = this.state.Payments.filter(l => l._id !== payments._id );
    this.setState({Payments:Payments});
  }



  render(){
    const {id, fullname, role, dateofbirth, address,phonenumber } = this.state;
  return (
    <React.Fragment>

        

        <br></br>
        <h2 className="heading">My Profile</h2>

        {/* <div className="center">
            <img src={leavepic} alt="leavepic"/>
         </div> */}
        
        <table>
          <tr style={{ backgroundColor: "#7121AD", color: "white" }}> My Full Name </tr>
          

        </table>
        
      

      </React.Fragment>
  
  );
  }
};

export default Profile;
