import React, { Component } from 'react';
import { Modal, Button, Row, Col, Form, FormGroup } from 'react-bootstrap';
import Snackbar from '@material-ui/core/Snackbar';
import IconButton from '@material-ui/core/IconButton';
import { Redirect } from 'react-router-dom';
// import Cleave from 'cleave.js/react';


export class AddPaymentsModal extends Component {
    constructor(props) {

        console.log("Run");
        super(props);
        this.state = { snackbaropen: false, snackbarmsg: '' };
        this.handleSubmit = this.handleSubmit.bind(this);
    }

    
    snackbarClose = (event) => {
        this.setState({ snackbaropen: false });
    };

    handleSubmit(event) {

        event.preventDefault();
        alert(event.target.name.value);
        fetch('http://localhost:5000/api/payment-details/', {
            method: 'POST',
            headers: {
                'Accept': 'application/json',
                'Content-Type': 'application/json'
                
            },
            body: JSON.stringify({
                name: event.target.name.value,
                bankname: event.target.bankname.value,
                accountnumber: event.target.accountnumber.value,
                branch: event.target.branch.value
            })
        })
            .then(res => res.json())
            .then((result) => {
                alert("Success!");
                console.log("result" , result)
            }, (error) => {
                this.setState({ snackbaropen: true, snackbarmsg: 'Failed' })
            }

            )
    }



  validate(){
        let PaymentsError = "";

        if(!this.state.PaymentsError){
            PaymentsError = "This Cannot Be Blank."
        }

        if(PaymentsError){
            this.setState({PaymentsError:PaymentsError})
            return false;
        }

        return true;
    }

   


    render() {
        return (
            <div className="container">


                <Snackbar anchorOrigin={{ vertical: 'top', horizontal: 'center' }}
                    open={this.state.snackbaropen}
                    autoHideDuration={6000}
                    onClose={this.snackbarClose}
                    message={<span id="message-id">{this.state.snackbarmsg}</span>}
                    action={[
                        <IconButton key="close" aria-label="Close" color="danger" onClick={this.snackbarClose}></IconButton>
                    ]}
                />
                <Modal
                    {...this.props}
                    size="lg"
                    aria-labelledby="contained-modal-title-vcenter"
                //centered
                >
                    <Modal.Header closeButton>
                        <Modal.Title id="contained-modal-title-vcenter">
                            Add Your Bank Details
                        </Modal.Title>
                    </Modal.Header>

                    <div class="alert alert-info" role="alert">
                            Note: Your bank details will be verified with the bank by the admin.
                    </div>
                    <Modal.Body>

                        <Row>
                            <Col sm={6}>
                                <Form onSubmit={this.handleSubmit}>
                                    <Form.Group controlId="name">
                                        <Form.Label>Your Full Name</Form.Label>
                                        <Form.Control type="text" name="name" required placeholder="Your full name..." className="form-field"/>
                                        <div style={{background:"#f8d7da"}}>{this.state.PaymentsError}</div>
                                    </Form.Group>

                                    <br></br>

                                    <Form.Group controlId="name">
                                        <Form.Label>Bank Name</Form.Label>
                                        <Form.Control type="text" name="bankname" required placeholder="Eg. Commercial Bank.." />
                                        <div style={{background:"#f8d7da"}}>{this.state.PaymentsError}</div>
                                    </Form.Group>

                                    <br></br>

                                    <Form.Group controlId="name">
                                        <Form.Label>Account Number</Form.Label>
                                        <Form.Control type="text" name="accountnumber" required placeholder="Account Number..." />
                                        <div style={{background:"#f8d7da"}}>{this.state.PaymentsError}</div>
                                    </Form.Group>

                                    <br></br>

                                    <Form.Group controlId="name">
                                        <Form.Label>Bank Branchr</Form.Label>
                                        <Form.Control type="text" name="branch" required placeholder="Bank Branch..." />
                                        <div style={{background:"#f8d7da"}}>{this.state.PaymentsError}</div>
                                    </Form.Group>
                                    
                                    <br></br>
                                    <Form.Group>
                                        <Button  style={{ backgroundColor: "#7121AD", color: "white" }} className="btn"  type="submit" >
                                            Add my Bank Details
                                        </Button>
                                    </Form.Group>
                                </Form>
                            </Col>
                        </Row>

                    </Modal.Body>
                    <Modal.Footer>
                        <Button variant="danger" onClick={this.props.onHide}>Close</Button>
                    </Modal.Footer>
                </Modal>
            </div>
        );
    }
}
